import { Component, ElementRef } from '@angular/core';
import { Router } from '@angular/router';
import { trigger, transition, style, animate } from '@angular/animations';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css'],
  animations: [
    trigger('pageFade', [
      transition(':enter', [
        style({ opacity: 0 }),
        animate('300ms', style({ opacity: 1 })),
      ]),
      transition(':leave', [
        animate('300ms', style({ opacity: 0 })),
      ]),
    ]),
  ],
})

export class InicioComponent {
  constructor(private elementRef: ElementRef, private router: Router) {}

  ngAfterViewInit() {
    const video = this.elementRef.nativeElement.querySelector('#background-video');

    video.style.position = 'fixed';
    video.style.top = '0';
    video.style.left = '0';
    video.style.width = '100%';
    video.style.height = '100%';
    video.style.opacity = '1'; // Asegúrate de que el video sea visible al principio
    video.playbackRate = 1;
  }

  navigateToOtherPage() {
    const video = this.elementRef.nativeElement.querySelector('#background-video');
    video.style.opacity = '0'; // Cambiar la opacidad para la animación de salida

    // Navegar a la otra página después de la animación
    setTimeout(() => {
      this.router.navigate(['/home']);
    }, 300); // El tiempo debe coincidir con el tiempo de la animación
  }
}
