import { Component, ElementRef } from '@angular/core';
import { trigger, state, style, animate, transition } from '@angular/animations';


@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css'],
  animations: [
    trigger('fadeInOut', [
      state('void', style({ opacity: 0 })),
      state('*', style({ opacity: 1 })),
      transition('void <=> *', animate('300ms ease-in-out')),
    ]),
  ],
})
export class InicioComponent {
  showDiv = false;

  startTransition() {
    this.showDiv = true;
  }

  constructor(private elementRef: ElementRef) {}
  ngAfterViewInit() {
    const video = this.elementRef.nativeElement.querySelector('#background-video');

    video.style.position = 'fixed';
    video.style.top = '0';
    video.style.left = '0';
    video.style.width = '100%';
    video.style.height = '100%';
    video.playbackRate = 1;
  }
}
<div class="relative">
    <video autoplay loop muted class="z-0 object-cover "id="background-video">
      <source src="../../../assets/imagenes/fondo2.mp4" type="video/mp4">
    </video>
    <div class="flex justify-center items-center">
        <button [routerLink]="['/home']" class="mt-12x transition-transform duration-300 transform hover:scale-110">
            <div class="z-10 p-3 tracking-widest text-2xl w-44 backdrop-blur-xl text-center text-white border-white border-2 rounded-full border-opacity-70  bg-opacity-20"> 
                <h1>START</h1>
            </div>
        </button>
      </div>      
  </div>
